import {createContext, useEffect, useState} from "react";
import axios from "axios";
export const AuthContext = createContext();

export const AuthContextProvider = ({children}) => {
    const [user, setUser] = useState(null);
    const [admin, setAdmin] = useState()
    const apiUrl = 'https://18.219.147.241';

    const getProfile = async () => {
        try {
            const apiUrl = 'https://18.219.147.241';
            const token = window.localStorage.getItem("jwt");
            const response = await axios.post(`${apiUrl}/api/get/profile`, {jwt: token});
            setUser(response.data);
        } catch (error) {
            console.error(error);
        }
    }

    const signup = async (inputs) => {
        const response = await axios.post(`${apiUrl}/api/auth/signup`, inputs);
    }

    const login = async (inputs) => {
        const response = await axios.post(`${apiUrl}/api/auth/login`, inputs);
        window.localStorage.setItem("jwt", response.data.message);
        console.log(response);
    }

    const logout = async (inputs) => {
        const response = await axios.post("api/auth/logout", inputs);
        setUser(null);
    }


    return (
        <AuthContext.Provider value={{login, logout, signup, getProfile, user}}>
            {children}
        </AuthContext.Provider>
    );
}
