/*
Title: Issues.js
Author: Joel Harawa
Purpose: Display the home page to the user
*/

import React, {useEffect, useState} from "react";
import Navbar from "../components/Navbar";
import axios from "axios";
import styled from "styled-components";
import {useParams} from "react-router-dom";


// Styled components
const Container = styled.div`
    display: flex;
    flex-direction: column;
`;

const IssuesBar = styled.div`
    height:8vh;
    background-color: white;
`;

const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex;
    align-items: center;
    justify-content: space-around;
`;

const Issue = styled.input`
    font-size: 18px;
    font-family: 'DM Sans', serif;
    font-weight: 600;
`;

const IssueContent = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;

const Cover = styled.img`
    width: 30vw;
    height: 50vh;
    object-fit: cover;   
`;

const CoverContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #ddd;
    width: 30vw;
    height: 50vh;
`;

const Metadata = styled.input`
    font-family: 'DM Sans', serif;
    width: 28vw;
    font-size: 20px;
    margin-top: 10px;
`;

const EditIssues = () => {
    const apiUrl = 'https://18.219.147.241';
    const [issues, setIssues] = useState(-1);
    const {postIndex} = useParams();
    const [posts, setPosts] = useState([]);
    const [postNumber, setPostNumber] = useState(parseInt(postIndex,10) || 0);

    // Decrease the post number when the "PREVIOUS POST" is clicked
    const handlePreviousClick = () => {
        if (postNumber > 0) {
            setPostNumber(postNumber-1);
        } else {
            setPostNumber(posts.length-1);
        }
    };

    const handleNextClick = () => {
        // Increase the post number when the "NEXT POST" is clicked
        if (postNumber === posts.length-1) {
            setPostNumber(0);
        } else {
            setPostNumber(postNumber+1)
        }
    };

    useEffect(() => {
        const getPosts = async() => {
            try {
                console.log("get that post")
                const response = await axios.get(`${apiUrl}/api/post/getIssue`);
                console.log(response.data);
                setIssues(response.data);
            } catch (error) {
                console.log(error, "This failed");
            }
        }
        getPosts();
    }, []);

    return (
        <>
            <Container>
            <IssuesBar>
                <Wrapper>
                    <Issue placeholder="Issue Edition..."/>
                </Wrapper>
            </IssuesBar>
            <IssueContent>
                <CoverContainer>
                </CoverContainer>
                <Metadata placeholder="Issue Title..."/>
            </IssueContent>
            </Container>
        </>
    );
}

export default EditIssues;
