import axios from "axios";
import React, { useEffect, useState } from "react";
import styled from "styled-components";
import Navbar from "../components/Navbar";

const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 100%
    height: 100%;
`;

const Header = styled.div`
    display: flex;
    align-items: center;
`;

const Title = styled.h1`
    font-family: "Poppins", sans-serif;
`;


const Posts = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 80%;
`;

const PostImageContainer = styled.div`
    height: 100%;
    width: 15%; 
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const PostImage = styled.img`
    object-fit: cover;
    width: 100%;
    height: 100%;
`;

const PostTitle = styled.span`
    font-family: "Poppins", sans-serif;
    font-size: 36px;
    padding-top: 8px;
`;

const PostAuthor = styled.span`
    font-family: "Poppins", sans-serif;
    font-size: 34px;
    padding-top: 8px;
`;

const Post = styled.div`
    display: flex;
    width: 90%;
    background-color: #f2f2f2;
    border-radius: 15px;
    overflow: hidden;
    height: 15vh;
    margin-top: 15px;
    &:hover{
        background-color: black;
        color: white;
        cursor: pointer;
    }
`;

const PostData = styled.div`
    display: flex;
    flex-direction: column;
    padding-left: 10px;
    height: 100%;
`;

const AllBlogPosts = () => {
    const apiUrl = 'https://18.219.147.241';
    const [posts, setPosts] = useState([]);
    useEffect(() => {
        const getPosts = async () => {
            try {
                const response = await axios.get(`${apiUrl}/api/get/getPosts`);
                console.log(response);
                setPosts(response.data);
            } catch (error) {
                console.log(error);
            }
        }
    }, [])

    return (
        <>
            <Navbar/>
            <Container>
                <Header>
                    <Title>All Blog Posts</Title>
                </Header>
                <Posts>
                    <Post>
                        <PostImageContainer>
                            <PostImage src={require("../images/IMG_1547.jpeg")}/>
                        </PostImageContainer>
                        <PostData>
                            <PostTitle>My CULTR General Meeting Takeaways</PostTitle>
                            <PostAuthor>King Charles</PostAuthor>
                        </PostData>
                    </Post>
                    <Post>
                        <PostImageContainer>
                            <PostImage src={require("../images/P1010492.JPG")}/>
                        </PostImageContainer>
                        <PostData>
                            <PostTitle>Valentine's Day Photoshoot</PostTitle>
                            <PostAuthor>Alicia Keys</PostAuthor>
                        </PostData>
                    </Post>
                </Posts>
            </Container>
        </>
    )
}

export default AllBlogPosts;
