import axios from "axios";
import { useEffect, useState } from "react";
import styled from "styled-components";

// Styled components
const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 60%;
`;

const Cover = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    height: 20vh;
    width: 100%;
`; 

const CoverImage = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;
`;

const Head = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    height: 8vh;
    width: 90%;
`;

const Author = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
`;

const AuthorName = styled.a`
    font-family: "Poppins", sans-serif;
    font-size: 18px;
    padding-left: 5px;
`;

const Profile = styled.div`
    border-radius: 50%;
    height: 40px;
    width: 40px; 
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const ProfileImage = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;
`;

const Time = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
`;

const TimeText = styled.a`
    font-family: "Poppins", sans-serif;
    font-size: 18px;
`;

const Content = styled.div`
    display: flex;
    width: 90%;
`;

const Text = styled.p`
    font-family: "Poppins", sans-serif;
    font-size: 18px;
`;

const Title = styled.h1`
    font-family: "Poppins", sans-serif;
`;


const BlogPost = () => {
    const apiUrl = 'https://18.219.147.241';
    const [posts, setPosts] = useState([]);
    useEffect(()=> {
        const getPosts = async () => {
            try {
            const response = await axios.get(`${apiUrl}/api/get/getPost`);
            console.log(response);
            setPosts(response.data);
            } catch (error) {
                console.error(error);
            } 
        }
        getPosts();
    }, [])
    const index = posts.length - 1;
    if (posts.length) {
        return (
            <Container>
                <Cover>
                    <CoverImage src={posts[index].photoUrl}/>
                </Cover>
                <Head>
                    <Title>{posts[index].title}</Title>
                </Head>
                <Head>
                    <Author>
                        <Profile>
                            <ProfileImage src={require("../images/P1010492.JPG")}/>
                        </Profile>
                        <AuthorName>{posts[index].firstName} {posts[index].lastName}</AuthorName>
                    </Author>
                    <Time>
                        <TimeText>6/25/2024</TimeText>
                    </Time>
                </Head>
                <Content>
                    <Text>{posts[index].body}</Text>
                </Content>
            </Container>
        )
    }
}

export default BlogPost;
