import React, {useState} from "react";
import styled from "styled-components";
import axios from "axios";

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const SubtitleBox = styled.div`
    width: 80vw;
`;

const Title = styled.h1`
  font-family: 'Archivo Black', sans-serif;
`;

const Subtitle = styled.h2`
  font-family: 'Archivo Black', sans-serif;
`;

const Intro = styled.textarea`
    height: 20vh;
    width: 80vw;
    padding: 5px;
    font-family: 'Cormorant Garamond', serif;
    font-size: 18px;
`;

const Input = styled.input`
    width: 80vw;
    height: 3vh;
    padding: 5px;
    font-family: 'Cormorant Garamond', serif;
    font-size: 18px;
`;

const Button = styled.button`
  background-color: black;
  color: white;
  cursor: pointer;
  padding: 1.5vh 5vw;
  font-family: 'Cormorant Garamond', serif;
  border: none;
  font-size: 15px;
`;

const ImageContainer = styled.div`
    display: flex;
    flex-direction: column;
`;
const Photo = styled.input`
    display: none;
`;

const PhotoLabel = styled.label`
    background-color: black;
    color: white;
    cursor: pointer;
    padding: 1.5vh 5vw;
    font-family: 'Cormorant Garamond', serif;
    font-size: 16px;
`;

const ImagePreview = styled.img`
  max-width: 100%;
  max-height: 300px;
  margin-top: 10px;
`;

const Date = styled.input`
  width: 80vw;
  height: 3vh;
  padding: 5px;
  font-family: 'Cormorant Garamond', serif;
  font-size: 18px;
`;

const EditAbout = () => {
    const apiUrl = 'https://18.219.147.241';
    const [intro, setIntro] = useState("");
    const [photo, setPhoto] = useState("");
    const [tag, setTag] = useState("");
    const handleSubmit = async () => {
        try {
            const article = new FormData();
            article.append("intro", intro);
            article.append("category", category);
            article.append("date", date);
            article.append("tag", tag);

            const response = await axios.post(`${apiUrl}/api/post/addPost`, article, {
                headers: {"Content-Type" : "multipart/form-data"},
            });
            console.log("Article successfully submitted:", response.data);
        } catch (error) {
            console.error("Error submitting article");
        }
    }


    const handleCategory = (e) => {
        setCategory(e.target.value)
    }

    const handleIntro = (e) => {
        setIntro(e.target.value);
    }

    const handleTag = (e) => {
        setTag(e.target.value);
    }
    const handleUpload = (e) => {
        setPhoto(e.target.files[0])


        const reader = new FileReader();
        reader.onloadend = () => {
            document.getElementById("imagePreview").src = reader.result;
            console.log("selected photo", photo);
        }
        if (photo) {
            reader.readAsDataURL(photo);
        }
    }

    const handleDate = (e) => {
        setDate(e.target.value);
    }
    return (
        <Container>
            <Title>About us</Title>
            <SubtitleBox>
                <Subtitle>Intro</Subtitle>
            </SubtitleBox>
            <Intro placeholder="Enter article title..."
                value={intro}
                onChange={handleIntro}/>
            <ImageContainer>
                {photo && <ImagePreview id="imagePreview" src={URL.createObjectURL(photo)} alt="Preview"/>}
                <PhotoLabel htmlFor="photoInput">Upload Photo</PhotoLabel>
                <Photo type="file" id="photoInput" accept="image/*" onChange={handleUpload}/>
            </ImageContainer>
            <Button onClick={handleSubmit}>Submit Article</Button>
        </Container>
    )
}
export default EditAbout;
