/*
Title: Blog.js
Author: Joel Harawa
Purpose: Display the current blog post to the user
*/

import react, { useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import ComingSoon from "../components/ComingSoon";
import BlogPost from "../components/BlogPost";
import styled from "styled-components";
import { AuthContext } from "../context/AuthContext";
import { 
    FaChevronLeft, FaChevronRight, FaNewspaper, FaPlus
 } from "react-icons/fa";

// Styled components
const Container = styled.div`
    display: flex;
    width: 100%;
    height: 100%;
`;

const Left = styled.div`
    display: flex;
    width: 20%;
`;

const Right = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 20%;
    height: 100%;
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: space-between;
    width: 90%;
    position: absolute;
    position: fixed;
    top: 50%;
`;

const Arrow = styled.button`
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    border-radius: 50%;
    border: none;
    padding: 18px;
    font-size: 18px;
    opacity: 0.5;
    &:hover {
        opacity: 1;
    }
`;

const Actions = styled.div`
    display: flex;
    width 100%;
    height: 10%;
    align-items: center;
`;

const Button = styled.button`
    border: 1px solid black;
    background-color: white;
    display: flex;
    align-items: center;
    font-family: "Poppins", sans-serif;
    font-size: 18px;
    padding: 5px;
    margin-top: 10px;
    margin-left: 10px;
    &:hover {
        background-color: black;
        color: white;
        cursor: pointer;
    }
`;

const Blog = () => {
    const { user } = useContext(AuthContext);
    const navigate = useNavigate();
    /* Need to make functions to get the current post and functions to
       get the next post 
    */
    const addPost = () => {
        if (!user) {
            navigate("/login");
        } else {
            navigate("/addpost")
        }
    }

    const allPosts = () => {
        navigate("/allposts")
    }

    return(
        <>
            <Navbar/>
            <Container>
                <ButtonContainer>
                    <Arrow>
                        <FaChevronLeft/>
                    </Arrow>
                    <Arrow>
                        <FaChevronRight/>
                    </Arrow>
                </ButtonContainer>
                <Left/>
                <BlogPost/>
                <Right>
                    <Actions>
                        <Button>
                            <FaPlus/>
                            &nbsp;
                            Add Post
                        </Button>
                        <Button>
                            <FaNewspaper/>
                            &nbsp;
                            All Posts
                        </Button>
                    </Actions>
                </Right>
            </Container> 
        </>
    );
}

export default Blog;
